const largestProduct = (numStr) => {
  // make an array of numbers
  const numsArr = numStr.split('').map(x => parseInt(x))
  console.log(numsArr.slice(0,13))
  
  // const productCheck = (arr) => {
  //   return arr.reduce((a, b) => a * b)
  // }

  let currentLeader = 0
  while (numsArr.length > 12) {
    if (numsArr.slice(0, 13).reduce((a, b) => a * b) > currentLeader) currentLeader = numsArr.slice(0, 13).reduce((a, b) => a * b)
    numsArr.shift()
  }

  return currentLeader

}

